scriptItem.expression:
node = kwargs["node"]
if node.type().name()=="block_begin":
    if node.parm("method").evalAsString()=="metadata":
        return True
return False

if hou.getenv("MASSE_MULTIPARM_FOLDER"):
    return True
return False

if len(hou.selectedNodes()) == 2:
    return True
return False

if kwargs["node"].type().name()=="wedge":
    return True
